import React, { useState, useEffect } from "react";
import TextLoop from "react-text-loop";
import {Button, ButtonToolbar} from 'rsuite';
import { Popover, Whisper } from 'rsuite';



function Title(props) {

    return (
        <div class="h-screen w-screen" style = {{ background : 'lightpink', display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center'}}>
            <div class="w-64 flex justify-end">
                <h1 class = "font-sans text-sm font-hairline text-white">
                    Welcome to
                </h1> 
            </div>
            <h1 class = "leading-none font-sans text-6xl text-white uppercase">
                Consensus
            </h1>
            <div class="text-white font-sans">
                Decide where to {" "} 
                <TextLoop
                    springConfig={{ stiffness: 70, damping: 31 }}
                    adjustingSpeed={500}
                >
                    <span>travel</span>
                    <span>eat</span>
                    <span>hang out</span>
                </TextLoop>
            </div>
            <ButtonToolbar>
                <Whisper placement="top" trigger="hover" speaker={speaker}>
                    <Button>Hover</Button>
                </Whisper>
            </ButtonToolbar>
        </div>
    );

}

export default Title;
