import React, { useState, useEffect } from "react";
import { Transition, animated } from 'react-spring'
import database from '../firebase';
import Loader from "./loader"
import Carousel from "nuka-carousel";
import { useSprings } from "react-spring/hooks";
import ReactDOM from 'react-dom'

import '../styles/Results.css'

function Place({i, data}){
    const { name, vote, photos } = data;
    const pic = photos[0];
    console.log(data)
    console.log(i)
    var state = { show: true };
    if (i == 0){
        return (
            <div className = "result">
                <Transition
                    native
                    items={state.show}
                    from={{ overflow: 'hidden', height: 0 }}
                    enter={[{ height: 'auto' }]}
                    leave={{ height: 20 }}>
                    {show =>
                        show && (props => <animated.div style={props}>
                            <div className = "result"></div>
                            <img id='circle' src={pic} alt="profilePicture" />
                        </animated.div>)
                    }
                </Transition>
                <Transition
                    native
                    items={state.show}
                    from={{ overflow: 'hidden', height: 0 }}
                    enter={[{ height: 'auto' }]}
                    leave={{ height: 5 }}>
                    {show =>
                        show && (props => <animated.div style={props}>
                            <div style = {{overflow: 'hidden', width: '100%' , color: 'white' , display: 'flex' , justifyContent: 'center', alignItems: 'center', fontSize: '2em', fontFamily: "'Kanit', sans-serif", textTransform: 'uppercase'}}>
                                {name} {vote}
                            </div>
                        </animated.div>)
                    }
                </Transition>
            </div>
            // <div/>
        );
    } else {
        return;
    }
}

function Results(props) {

    const [data, setData] = useState([])
    const [loading, setLoading] = useState(true)
    //const i = props.i

    useEffect(() => {
        // var arr = []
        database.ref(`groups/${props.groupCode}/data`).once("value", (snapshot) => {
            snapshot.forEach(item => {
                var temp = item.val()
                data.push(temp)
            })
            data.sort((a,b) => b.vote - a.vote)
            // setData(data.concat(arr))
            // console.log("arr")
            // console.log(arr)
            console.log("data")
            console.log(data)
            setLoading(false)
        })
    }, [props.groupCode])

    // async function filldata(){
    //     database.ref(`groups/${props.groupCode}/data`).once("value", (snapshot) => {
    //         snapshot.forEach(item => {
    //             var temp = item.val()
    //             data.push(temp)
    //         })
    //         data.sort((a,b) => b.vote - a.vote)
    //         setLoading(false)
    //     })
    //     console.log(data)
    // }

    if (loading) return <Loader loading={true} />

    return(
        <div class="h-screen w-screen" style = {{ background : 'black', display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center'}}>
                <Place i = {0} data = {data[1]}/>
        </div>
    );
}

export default Results;
