        <div className="ml-6 pt-1">
          <h1 className="text-2xl text-blue-700 leading-tight">
            Consensus
          </h1>
          <p className="text-base text-gray-700 leading-normal">
            Vote for shit i guess lol
          </p>
        </div>
      </div>
