import React, { useState, useEffect } from "react";
import database from '../firebase';

function Results(props) {

    const [data, setData] = useState([])

    useEffect(() => {
        var arr = []
        database.ref(`groups/${props.groupCode}`).once("value", (snapshot) => {
            snapshot.forEach((data) => {
                arr.push(data.val())
            })
        })
        console.log(arr)
        arr.sort((a,b) => b.vote - a.vote)
        console.log(sort)
        setData(data.concat(sort))
    }, [props.groupCode])

    return(
        <div class="h-screen w-screen" style = {{ background : 'lightpink', display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center'}}/>
    );
}

export default Results;
