  //         <Search />
  //       }/>
  //       <Route path="/:groupCode" exact strict component={({match}) =>
  //         <div>
  //           <Poll groupCode={match.params.groupCode} />
  //         </div>
  //       }/>
  //     </Switch>
  //   </Router>
  // );
}

export default App;
