import React, { useState, useEffect } from "react";
import { Transition, animated } from 'react-spring'
import database from '../firebase';
import Carousel from "nuka-carousel";
import { useSprings } from "react-spring/hooks";
import ReactDOM from 'react-dom'

import '../styles/Results.css'



function Place({i, data}){
    const { name, vote, photos } = data[i];
    var state = { show: true };
    if (i <= 2){
        return (
            <div>
                <Transition
                    native
                    items={state.show}
                    from={{ overflow: 'hidden', height: 0 }}
                    enter={[{ height: 'auto' }]}
                    leave={{ height: 0 }}>
                    {show =>
                        show && (props => <animated.div style={props}>
                            <div className = 'rows' style = {{ height : 70, opacity: 1, display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center'}}>
                                <div className = "circle">
                                <Carousel className="topCard">
                                    {photos.map((pic, index) => (
                                        <img src={pic} key={index} alt="profilePicture" />
                                    ))}
                                </Carousel>
                                </div>
                                <div style = {{overflow: 'hidden', width: '100%' , color: 'white' , display: 'flex' , justifyContent: 'center', alignItems: 'center', fontSize: '2em', fontFamily: "'Kanit', sans-serif", textTransform: 'uppercase'}}>
                                    {name} with a vote of {vote}
                                </div>
                            </div>
                        </animated.div>)
                    }
                </Transition>
                <div style = {{height : 20}}/>
            </div>
        );
    } else {
        return(
            <div/>
        );
    }
}

function Results(props) {

    const [data, setData] = useState([])

    useEffect(() => {
        var arr = []
        database.ref(`groups/${props.groupCode}/data`).once("value", (snapshot) => {
            snapshot.forEach(item => {
                var temp = item.val()
                data.push(temp)
            })
            arr.sort((a,b) => b.vote - a.vote)
            setData(data.concat(arr))
            console.log(arr)
        })
    }, [props.groupCode])

    return(
        <div class="h-screen w-screen" style = {{ background : 'black', display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center'}}>
            {data.map(({}, i) => (
                <Place
                    i = {i}
                    data = {data}
                />
            ))}
        </div>
    );
}

export default Results;
