import React, { Component } from 'react';

const style = {
  width: '100%',
  height: '100%',
  left: 0,
  top: 0,
  margin: 0,
  padding: 0,
  position: 'absolute',
};

export default class GoogleMapMap extends Component {
  shouldComponentUpdate() {
    return false; // disable react on this div
  }

  render() {
    const { registerChild } = this.props;
    return <div ref={registerChild} style={style} />;
  }
}
import EventEmitter from 'eventemitter3';

export default class MarkerDispatcher extends EventEmitter {
  constructor(gmapInstance) {
    super();
    this.gmapInstance = gmapInstance;
  }

  getChildren() {
    return this.gmapInstance.props.children;
  }

  getMousePosition() {
    return this.gmapInstance.mouse_;
  }

  getUpdateCounter() {
    return this.gmapInstance.updateCounter_;
  }

  dispose() {
    this.gmapInstance = null;
    this.removeAllListeners();
  }
}
// https://github.com/acdlite/recompose/blob/master/src/packages/recompose/utils/omit.js
const omit = (obj, keys) => {
  const { ...rest } = obj;
  for (let i = 0; i < keys.length; i++) {
    const key = keys[i];
    if (key in rest) {
      delete rest[key];
    }
  }
  return rest;
};

export default omit;
